import { createSlice } from '@reduxjs/toolkit';

export const loaderSlice = createSlice({
    name: 'loader',
    initialState: {
        isOpen: false,
        isLoading: false
    },
    reducers: {
        toggleLoader: state => {
            if (!state.isLoading) { state.isOpen = !state.isOpen}
        },
        isLoading: state => {
            if (state.isOpen) { state.isLoading = true}
            else { state.isLoading = false }
        }
    }
})

export const { toggleLoader, isLoading } = loaderSlice.actions

export default loaderSlice.reducer

export const selectLoader = state => state.loader.isOpen
