import React, { useContext } from "react";
import styled from "styled-components";
import { Link, useLocation, useHistory } from "react-router-dom";
import { AuthContext } from "../../contexts";
import { setAuthToken } from "../../utils";

const HeaderContainer = styled.div`
  height: 100px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: fixed;
  background: #fff;
`;
const Brand = styled.span`
  font-size: 25px;
  margin-left: 20px;
  margin-right: 40px;
`;
const Block = styled(Link)`
  margin: 0px 20px;
  text-decoration: none;
  color: black;
  cursor: pointer;
  font-size: 11px;
    padding-top: 1.2rem;
    padding-bottom: 1.2rem;
    padding-left: 25px;
    padding-right: 25px;
    color: rgba(0, 0, 0, 0.7);
    color: #aaaaaa;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 3px;
  ${(props) =>
    props.$active &&
    `
    color:black;
    padding-bottom:2px;
    `}
`;

const NavSection1 = styled.div`
  display: flex;
  align-items: center;
`;

const NavSection2 = styled.div``;

export default function Header() {
  const history = useHistory();
  const location = useLocation(); //現在處於的 url
  const { user, setUser } = useContext(AuthContext);

  const handleLogout = () => {
    setAuthToken("");
    setUser("");
    if (location.pathname !== "/") {
      history.push("/");
    }
  };

  return (
    <HeaderContainer>
      <NavSection1>
        <Brand>Your first blog</Brand>
        <Block to="/" $active={location.pathname === "/"}>
          Home
        </Block>
        <Block to="/aboutMe" $active={location.pathname === "/aboutMe"}>
          About
        </Block>
        {user && (
          <Block to="/newPost" $active={location.pathname === "/newPost"}>
            Create new post
          </Block>
        )}
      </NavSection1>
      <NavSection2>
        <Block to="/register" $active={location.pathname === "/register"}>
          Register
        </Block>
        {!user && (
          <Block to="/login" $active={location.pathname === "/login"}>
            Login
          </Block>
        )}
        {user && <Block onClick={handleLogout}>登出</Block>}
      </NavSection2>
    </HeaderContainer>
  );
}
