import React, { useContext } from "react";
import styled from "styled-components";
import { Link, useLocation, useHistory } from "react-router-dom";
import { AuthContext } from "../../contexts";
import { setAuthToken } from "../../utils";

const HeaderContainer = styled.div`
  position: fixed;
  height: 10vh;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #fff;
  z-index:1;
`;
const Brand = styled.span`
  font-size: 25px;
  margin-left: 20px;
  margin-right: 40px;
  font-weight: 800;
  text-transform: uppercase;
`;
const Block = styled(Link)`
  margin: 0px 5%;
  text-decoration: none;
  color: black;
  cursor: pointer;
  font-size: 11px;  
  color: #aaaaaa;
  text-transform: uppercase;
  letter-spacing: 3px;
  ${(props) =>
    props.$active &&
    `
    color:black;
    `}
`;

const NavSection1 = styled.div`
  display: flex;
  align-items: center;
  width:40%;
`;

const NavSection2 = styled.div`
  width:60%;
`;

export default function Header() {
  const history = useHistory();
  const location = useLocation(); //現在處於的 url
  const { user, setUser } = useContext(AuthContext);

  const handleLogout = () => {
    setAuthToken("");
    setUser("");
    if (location.pathname !== "/") {
      history.push("/");
    }
  };

  return (
    <HeaderContainer>
      <NavSection1>
        <Brand>Your first blog.</Brand>
      </NavSection1>
      <NavSection2>
      <Block to="/" $active={location.pathname === "/"}>
        Home
      </Block>
      <Block to="/aboutMe" $active={location.pathname === "/aboutMe"}>
        About
      </Block>
      {user && (
      <Block to="/newPost" $active={location.pathname === "/newPost"}>
        Create new post
      </Block>
      )}
      <Block to="/register" $active={location.pathname === "/register"}>
        Register
      </Block>
      {!user && (
      <Block to="/login" $active={location.pathname === "/login"}>
        Login
      </Block>
      )}
      {user && (
      <Block to="/login" onClick={handleLogout}>
        Logout
      </Block>
      )}
      </NavSection2>
    </HeaderContainer>
  );
}
